import React, { useState } from 'react';

const AdminLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleAuthentication = (e) => {
    e.preventDefault();

    // Realiza la lógica de autenticación y envía los datos al servidor
    const credentials = {
      username: username,
      password: password
    };

    // Envía la solicitud al servidor (debes ajustar la URL y el método según tu backend)
    fetch('http://127.0.0.1:8000/admin/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    })
      .then(response => response.json())
      .then(data => {
        // Verifica la respuesta del servidor y maneja la lógica de autenticación
        if (data.success) {
          console.log('Autenticación exitosa');
          // Realiza acciones adicionales después de la autenticación, como redireccionar a la página del administrador
        } else {
          console.log('Autenticación fallida');
          // Maneja el caso de autenticación fallida, como mostrar un mensaje de error o limpiar los campos del formulario
        }
      })
      .catch(error => {
        console.error('Error al autenticar:', error);
        // Maneja el caso de error en la solicitud, como mostrar un mensaje de error
      });

    // Restablece los campos del formulario
    setUsername('');
    setPassword('');
  };

  return (
    <div className="admin-login">
      <h2>Admin Login</h2>
      <form onSubmit={handleAuthentication}>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
