import React, { Component } from 'react';
import axios from 'axios';

class Login extends Component {
  state = {
    username: '',
    password: '',
    isLoggedIn: false,
    error: '',
  };

  handleLogin = () => {
    const { username, password } = this.state;
    axios
      .post('http://127.0.0.1:8000/api/login/', { username, password })
      .then((response) => {
        if (response.status === 200) {
          this.setState({ isLoggedIn: true, error: '' });
        } else {
          this.setState({ error: 'Invalid response from the server' });
        }
      })
      .catch((error) => {
        if (error.response) {
          this.setState({ error: error.response.data.detail });
        } else {
          this.setState({ error: 'An error occurred while processing your request' });
        }
      });
  };

  render() {
    const { username, password, isLoggedIn, error } = this.state;
    return (
      <div>
        <h1>Login</h1>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => this.setState({ username: e.target.value })}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => this.setState({ password: e.target.value })}
        />
        {error && <p>{error}</p>}
        <button onClick={this.handleLogin}>Login</button>
        {isLoggedIn && <a href="/admin/">Go to admin panel</a>}
      </div>
    );
  }
}

export default Login;
